import React, { useState } from "react";
import "./Dicegame.css";

function random(n) {
  // Ìï®ÏàòÎäî 1Î∂ÄÌÑ∞ ~n ÏÇ¨Ïù¥Ïùò ÏûÑÏùòÏùò Ï†ïÏàòÎ•º Î∞òÌôò -> Ï£ºÏÇ¨ÏúÑÎ•º ÎçòÏßÑ Í≤∞Í≥ºÎ•º ÏÉùÏÑ±
  return Math.ceil(Math.random() * n);
}
//math.ceil() Ìï®ÏàòÎäî Ï£ºÏñ¥ÏßÑ Ïà´ÏûêÎ≥¥Îã§ ÌÅ¨Í±∞ÎÇò Í∞ôÏùÄ Ïà´Ïûê Ï§ë Í∞ÄÏû• ÏûëÏùÄ Ïà´ÏûêÎ•º IntegerÎ°ú Î∞òÌôòÌïúÎã§.

function Dicegame() {
  const [myNowRoll, setMyNowRoll] = useState(0); //Ï¥ùÌï©
  const [youNowRoll, setYouNowRoll] = useState(0);
  const [myScore, setMyScore] = useState(0); //Ï£ºÏÇ¨ÏúÑ ÎçòÏ†∏ÏÑú ÎçîÌïú Í∞í
  const [youScore, setYouScore] = useState(0);

  const handleRollClick = () => {
    //Ï£ºÏÇ¨ÏúÑ ÎçòÏßÄÍ∏∞ Î≤ÑÌäº
    const nextNum = random(6);
    const nextYouNum = random(6);

    //Ï£ºÏÇ¨ÏúÑÎ•º ÎçòÏ†∏ÏÑú ÎÇòÏò® Í∞íÏùÑ ÌòÑÏû¨ Ï†êÏàòÏóê ÎçîÌï®
    setMyScore(myScore + nextNum);
    setYouScore(youScore + nextYouNum);

    if (setMyScore >= 2) {
    } else if (setMyScore < 1) {
      return 0;
    }
  };

  const handleClearClick = () => {
    //Ï¥àÍ∏∞Ìôî
    setMyScore(0); //ÌîåÎ†àÏù¥Ïñ¥ 1Ïùò Ï†êÏàò Ï¥àÍ∏∞Ìôî
    setYouScore(0); //ÌîåÎ†àÏù¥Ïñ¥ 2Ïùò Ï†êÏàò Ï¥àÍ∏∞Ìôî
  };

  return (
    <div className="wrapper">
      {/* ÌîåÎ†àÏù¥Ïñ¥ 1 */}
      <section className="player player-0 player-active">
        <h2 name="player1" id="name-0">
          Player 1
        </h2>
        <p className="score" id="score-0">
          {myNowRoll}
        </p>
        <div className="current">
          <p className="current-label">Current</p>
          <p className="current-score" id="current--0">
            {myScore}
          </p>
        </div>
      </section>

      {/* ÌîåÎ†àÏù¥Ïñ¥ 2 */}
      <section className="player player-1 player-active">
        <h2 name="player2" id="name-1">
          Player 2
        </h2>
        <p className="score" id="score-1">
          {youNowRoll}
        </p>
        <div className="current">
          <p className="current-label">Current</p>
          <p className="current-score" id="current-1">
            {youScore}
          </p>
        </div>
      </section>

      <div id="board">
        <button className="btn btn-new" onClick={handleClearClick}>
          üîÑ New game
        </button>
        <button className="btn btn-roll" onClick={handleRollClick}>
          üé≤ Roll dice
        </button>
        <button className="btn btn-hold">üì• Hold</button>
      </div>
    </div>
  );
}

export default Dicegame;
