import React, { useState } from "react";
import "./Dicegame.css";

function random(n) {
  // Ìï®ÏàòÎäî 1Î∂ÄÌÑ∞ ~n ÏÇ¨Ïù¥Ïùò ÏûÑÏùòÏùò Ï†ïÏàòÎ•º Î∞òÌôò -> Ï£ºÏÇ¨ÏúÑÎ•º ÎçòÏßÑ Í≤∞Í≥ºÎ•º ÏÉùÏÑ±
  return Math.ceil(Math.random() * n);
}
//math.ceil() Ìï®ÏàòÎäî Ï£ºÏñ¥ÏßÑ Ïà´ÏûêÎ≥¥Îã§ ÌÅ¨Í±∞ÎÇò Í∞ôÏùÄ Ïà´Ïûê Ï§ë Í∞ÄÏû• ÏûëÏùÄ Ïà´ÏûêÎ•º IntegerÎ°ú Î∞òÌôòÌïúÎã§.

function Dicegame() {
  const [myScore, setMyScore] = useState(0); //Ï£ºÏÇ¨ÏúÑ ÎçòÏ†∏ÏÑú ÎçîÌïú Í∞í
  const [youScore, setYouScore] = useState(0);
  const [myTotalScore, setMyTotalScore] = useState(0); //Ï¥ùÌï©
  const [youTotalScore, setYouTodalScore] = useState(0);
  const [currentRoll, setCurrentRoll] = useState(0); //Ï£ºÏÇ¨ÏúÑÎ•º ÎçòÏ†∏ÏÑú ÎÇòÏò® Í∞í
  const [currentPlayer, setCurrentPlayer] = useState(1); //ÌòÑÏû¨ ÌÑ¥Ïù∏ ÌîåÎ†àÏù¥Ïñ¥

  const handleRollClick = () => {
    //Ï£ºÏÇ¨ÏúÑ ÎçòÏßÄÍ∏∞ Î≤ÑÌäº
    const nowNum = random(6);
    const nextNum = random(6);
    setCurrentRoll(nowNum);

    //Ï£ºÏÇ¨ÏúÑÎ•º ÎçòÏ†∏ÏÑú ÎÇòÏò® Í∞íÏùÑ ÌòÑÏû¨ Ï†êÏàòÏóê ÎçîÌï®
    if (nowNum >= 2) {
      if (currentPlayer === 1) {
        setMyScore(myScore + nowNum);
      } else {
        setYouScore(youScore + nextNum);
      }
    } else if (nowNum === 1) {
      if (currentPlayer === 1) {
        setMyScore(0);
      } else {
        setYouScore(0);
      }
      setCurrentPlayer(currentPlayer === 1 ? 2 : 1);
    } else {
      //0Ïù∏ Í≤ΩÏö∞Îäî ÏóÜÏßÄÎßå ÏïàÏ†ÑÌïòÍ≤å Ï≤òÎ¶¨ Ìï¥ÏïºÌïúÎã§.
      setCurrentPlayer(currentPlayer === 1 ? 2 : 1); //ÌÑ¥ ÎÑòÍ∏∞Í∏∞
    }
  };

  const handleClearClick = () => {
    //Ï¥àÍ∏∞Ìôî
    if (currentPlayer === 1) {
      setMyTotalScore(myTotalScore + myScore);
      setMyScore(0);
    } else {
      setYouTodalScore(youTotalScore + youScore);
      setYouScore(0);
    }
  };

  return (
    <div className="wrapper">
      {/* ÌîåÎ†àÏù¥Ïñ¥ 1 */}
      <section
        className={`player player-0 ${
          currentPlayer === 1 ? "player-active" : ""
        }`}
      >
        <h2 name="player1" id="name-0">
          Player 1
        </h2>
        <p className="score" id="score-0">
          {myScore}
        </p>
        <div className="current">
          <p className="current-label">Current</p>
          <p className="current-score" id="current--0">
            {currentPlayer === 1 ? myScore : 0}
          </p>
        </div>
      </section>

      {/* ÌîåÎ†àÏù¥Ïñ¥ 2 */}
      <section
        className={`player player-1 ${
          currentPlayer === 2 ? "player-active" : ""
        }`}
      >
        <h2 name="player2" id="name-1">
          Player 2
        </h2>
        <p className="score" id="score-1">
          {youScore}
        </p>
        <div className="current">
          <p className="current-label">Current</p>
          <p className="current-score" id="current-1">
            {currentPlayer === 2 ? youScore : 0}
          </p>
        </div>
      </section>

      <div id="board">
        <button className="btn btn-new">üîÑ New game</button>
        <div className="btn roll-score">Ï£ºÏÇ¨ÏúÑ : {currentRoll}</div>
        <button className="btn btn-roll" onClick={handleRollClick}>
          üé≤ Roll dice
        </button>
        <button className="btn btn-hold" onClick={handleClearClick}>
          üì• Hold
        </button>
      </div>
    </div>
  );
}

export default Dicegame;
