import React, { useState } from "react";
import "./App.css";

function random(n) {
  return Math.ceil(Math.random() * n);
}
//math.ceil() 함수는 주어진 숫자보다 크거나 같은 숫자 중 가장 작은 숫자를 Integer로 반환한다.

function App() {
  function Button(props) {
    const [myDiscount, setMyDiscount] = useState([]);
    const [otherDiscount, setOtherDiscount] = useState([]);
  }

  const handleRollClick = () => {
    const nextNum = random(6);
    const nextOtherNUM = random(6);

    setMyDiscount([...myDiscount, nextNum]);
    setOtherDiscount([...otherDiscount, nextOtherNUM]);
  };

  const handleClearClick = () => {
    setMyDiscount([]);
    setOtherDiscount([]);
  };

  return (
    <div className="wrapper">
      <section class="player player-0 player-active">
        <h2 name="player1" id="name-0">
          Player 1
        </h2>
        <p class="score" id="score-0">
          0
        </p>
        <div class="current">
          <p class="current-label">Current</p>
          <p class="current-score" id="current--0">
            0
          </p>
        </div>
      </section>
      <section class="player player-1 player-active">
        <h2 name="player2" id="name-1">
          Player 2
        </h2>
        <p class="score" id="score-1">
          0
        </p>
        <div class="current">
          <p class="current-label">Current</p>
          <p class="current-score" id="current-1">
            0
          </p>
        </div>
      </section>

      <div id="board">
        <button class="btn btn-new" onClick={handleClearClick}>
          🔄 New game
        </button>
        <button class="btn btn-roll" onClick={handleRollClick}>
          🎲 Roll dice
        </button>
        <button class="btn btn-hold">📥 Hold</button>
      </div>
    </div>
  );
}

export default Dicegame;
